package com.syntax.class2;

public class ArithmaticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number1=10;
		int number2=20;
		System.out.println(number1+number2);
		System.out.println(number1-number2);
		System.out.println(number1*number2);
		System.out.println(number1/number2);
		System.out.println("*************");
		double number3=10;
		double number4=20;
		System.out.println(number3+number4);
		System.out.println(number3-number4);
		System.out.println(number3*number4);
		System.out.println(number3/number4);
		System.out.println("*************");
		boolean var1=true;
		boolean var2=false;
		// System.out.println(var1/var2); not allowed on boolean
		
		System.out.println("*************");
		char char1='A';
		char char2='B';
		System.out.println(char1+char2);
		System.out.println(char1-char2);
		System.out.println(char1*char2);
		System.out.println(char1/char2);
		
		System.out.println("*************");
		String str1="hi";
		String str2="bye";
		System.out.println(str1+str2);//-,*,/ not allowed on strings
		System.out.println("It is so windy today 10/26.");
		
		

	}

}
