package com.syntax.class2;

public class ShortHandOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 10;
		number = number + 2;
		System.out.println(number);
		number++;  // shorter way of line 9
		System.out.println(number);
		System.out.println("******************");
		number=10;
		number=number+30;//10+30=40
		System.out.println(number);
		number+=30; //number=40+30;
		System.out.println(number);
		number=10;
		number-=5;//10-5=5
		System.out.println(number);
		number*=5;//5*5=25
		System.out.println(number);
		number=10;
		number/=5;//10/5=2
		System.out.println(number);
		number=10;
		number%=5;//10/5=2 remainder 0
		System.out.println(number);
		
	}

}
